import styled , {css} from 'styled-components';

export const StyledLink = styled.a`

text-decoration:none;
color:red;
&:hover {
    color:green;
}


 ${({kristiansand}) => kristiansand && 
 
 css`
 
 background-color:black ;
 color:green;
 padding: 0.5rem 1rem;
 border-radius:1rem;
 &:hover {
    opacity: 0.5;
 }
 
 ` }
`;

export default  StyledLink ;
